import React, { useEffect, useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { fetchTodos, addTodo, updateTodo, deleteTodo } from "../todoSlice";
import { RootState, AppDispatch } from "./redux/store";

const TodoList: React.FC = () => {
  const dispatch = useDispatch<AppDispatch>();
  const todos = useSelector((state: RootState) => state.todos.items);
  const [newTask, setNewTask] = useState("");

  useEffect(() => {
    dispatch(fetchTodos());
  }, [dispatch]);

  const handleAddTodo = () => {
    if (newTask.trim() === "") return;
    dispatch(addTodo({ task: newTask, status: "Pending" }));
    setNewTask("");
  };

  const handleUpdateTodo = (id: string, task: string, status: string) => {
    dispatch(updateTodo({ id, task, status }));
  };

  const handleDeleteTodo = (id: string) => {
    dispatch(deleteTodo(id));
  };

  return (
    <div>
      <h1>TODO List</h1>
      <input
        type="text"
        value={newTask}
        onChange={(e) => setNewTask(e.target.value)}
        placeholder="Add a new task"
      />
      <button onClick={handleAddTodo}>Add</button>
      <ul>
        {todos.map((todo) => (
          <li key={todo.id}>
            <input
              type="text"
              value={todo.task}
              onChange={(e) => handleUpdateTodo(todo.id, e.target.value, todo.status)}
            />
            <select
              value={todo.status}
              onChange={(e) => handleUpdateTodo(todo.id, todo.task, e.target.value)}
            >
              <option value="Pending">Pending</option>
              <option value="Done">Done</option>
            </select>
            <button onClick={() => handleDeleteTodo(todo.id)}>Delete</button>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default TodoList;
