// src/store/todoSlice.ts
import { createSlice, createAsyncThunk } from "@reduxjs/toolkit";

const API_URL = "http://localhost:5000/todos";

export const fetchTodos = createAsyncThunk("todos/fetchTodos", async () => {
    const response = await fetch(API_URL);
    return response.json();
});

export const addTodo = createAsyncThunk("todos/addTodo", async (task: string) => {
    const response = await fetch(API_URL, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ task })
    });
    return response.json();
});

export const updateTodo = createAsyncThunk("todos/updateTodo", async ({ id, task, status }: { id: string, task?: string, status?: string }) => {
    const response = await fetch(`${API_URL}/${id}`, {
        method: "PUT",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ task, status })
    });
    return response.json();
});

export const deleteTodo = createAsyncThunk("todos/deleteTodo", async (id: string) => {
    await fetch(`${API_URL}/${id}`, { method: "DELETE" });
    return id;
});

const todoSlice = createSlice({
    name: "todos",
    initialState: { todos: [], status: "idle" },
    reducers: {},
    extraReducers: (builder) => {
        builder
            .addCase(fetchTodos.fulfilled, (state, action) => {
                state.todos = action.payload;
            })
            .addCase(addTodo.fulfilled, (state, action) => {
                state.todos.push(action.payload);
            })
            .addCase(updateTodo.fulfilled, (state, action) => {
                const index = state.todos.findIndex(todo => todo.id === action.payload.id);
                if (index !== -1) state.todos[index] = action.payload;
            })
            .addCase(deleteTodo.fulfilled, (state, action) => {
                state.todos = state.todos.filter(todo => todo.id !== action.payload);
            });
    }
});

export default todoSlice.reducer;
